import React, {Component} from 'react'

class Message extends Component{

    constructor() {
        super();
        this.state = {
            message: "Thank you for subscribing"
        };
        
    }

   
    changeMessage() {
        this.setState({
            message: "Thank you for Subscribing"
        })
    }

    
    

    render() {
        return(
            <div>
                <h1>Welcome visitor</h1>
                <button onClick = {() => this.changeMessage()}>Subscribe</button>
            </div>
            
        )
    }
}



export default Message;
