import React, { Component } from 'react'

export class Counter extends Component {

    constructor(props) {
      super(props)
    
      this.state = {
         count: 0
      }
    }

    increment() {
        this.state.count = this.state.count 
    }
    

  render() {
    return (
      <div>
        <div>{this.state.count}</div>
        <button onClick = {() => this.increment()}>Increment</button>
      </div>
    )
  }
}

export default Counter
