import React, { Component } from 'react'

export class HoverCenter extends Component {

  constructor(props) {
    super(props)
  
    this.state = {
       count:0
    }
  }

  incrementCount = () => {
    this.setState(prevState => {
      return{count: prevState.count + 1}
    })
  }
  

  render() {
    const {count} = this.state
    return <h2 onMouseOver={this.incrementCount}>Hoverd {count} times</h2>
  }
}

export default HoverCenter
