class Form extends Component {
    //Components state (To controlled the value)
    
    constructor(props) {
      super(props)
    
      this.state = {
         username: '',
         comments: '',
         topic: ''
      }
    }

    handleUsernameChange = (event) => {
      this.setState({
        username: event.target.value
      })
    }

    handleCommentsChange = (event) => {
      this.setState({
        comments: event.target.value
      })
    }

