import React, { Component } from 'react'

class ClassCounter extends Component {
    //state varible
    constructor(props) {
      super(props)
    
      this.state = {
         count: 0
      }
    }

    //method
    incrementCount = () => {
        this.setState({
            count: this.state.count + 1
        })
    }
    
  render() {
    return (
      <div>
        <button onClick={this.incrementCount}>Count {}</button>
      </div>
    )
  }
}

export default ClassCounter
